// lib/blocs/safety/bloc/safety_event.dart
import 'package:equatable/equatable.dart';
import '../../../modules/system_operation_also_main_module/models/safety_error.dart';
import '../../base/base_bloc_event.dart';

sealed class SafetyEvent extends BaseBlocEvent {
  const SafetyEvent();
}

final class SafetyStartMonitoring extends SafetyEvent {}

final class SafetyStopMonitoring extends SafetyEvent {}

final class SafetyErrorAdded extends SafetyEvent {
  final SafetyError error;

  const SafetyErrorAdded(this.error);
}

final class SafetyErrorResolved extends SafetyEvent {
  final String errorId;

  const SafetyErrorResolved(this.errorId);
}

final class SafetyErrorsLoaded extends SafetyEvent {}

final class SafetyThresholdUpdated extends SafetyEvent {
  final String componentId;
  final String parameter;
  final double minValue;
  final double maxValue;

  const SafetyThresholdUpdated({
    required this.componentId,
    required this.parameter,
    required this.minValue,
    required this.maxValue,
  });
}
