// lib/features/auth/domain/repositories/i_auth_repository.dart
abstract class IAuthRepository {
  Future<Either<AuthFailure, User>> signIn(String email, String password);
  Future<Either<AuthFailure, User>> signUp(UserSignUpParams params);
  Future<Either<AuthFailure, Unit>> signOut();
  Stream<Option<User>> watchAuthState();
}
