// lib/features/auth/presentation/screens/settings_screen.dart
import 'package:flutter/material.dart';
import 'package:flutter_bloc/flutter_bloc.dart';
import '../bloc/settings/settings_bloc.dart';
import '../widgets/settings_section.dart';
import '../widgets/notification_settings_dialog.dart';
import '../../../../core/presentation/widgets/loading_overlay.dart';

@RoutePage()
class SettingsScreen extends StatelessWidget {
  const SettingsScreen({super.key});

  @override
  Widget build(BuildContext context) {
    return BlocConsumer<SettingsBloc, SettingsState>(
      listener: (context, state) {
        state.mapOrNull(
          failure: (state) {
            ScaffoldMessenger.of(context).showSnackBar(
              SnackBar(
                content: Text(state.failure.toString()),
                backgroundColor: Colors.red,
              ),
            );
          },
        );
      },
      builder: (context, state) {
        return LoadingOverlay(
          isLoading: state is Loading,
          child: Scaffold(
            appBar: AppBar(
              title: const Text('Settings'),
            ),
            body: state.maybeMap(
              loaded: (state) => _buildSettings(context, state.settings),
              orElse: () => const SizedBox.shrink(),
            ),
          ),
        );
      },
    );
  }

  Widget _buildSettings(BuildContext context, AppSettings settings) {
    return ListView(
      padding: const EdgeInsets.all(16),
      children: [
        SettingsSection(
          title: 'Appearance',
          children: [
            SwitchListTile(
              title: const Text('Dark Mode'),
              value: settings.isDarkMode,
              onChanged: (value) {
                context.read<SettingsBloc>().add(
                  SettingsEvent.themeChanged(value),
                );
              },
            ),
          ],
        ),
        const SizedBox(height: 16),
        SettingsSection(
          title: 'Language',
          children: [
            ListTile(
              title: const Text('Language'),
              subtitle: Text(_getLanguageName(settings.languageCode)),
              trailing: const Icon(Icons.chevron_right),
              onTap: () => _showLanguageDialog(context, settings.languageCode),
            ),
          ],
        ),
        const SizedBox(height: 16),
        SettingsSection(
          title: 'Notifications',
          children: [
            ListTile(
              title: const Text('Notification Settings'),
              subtitle: const Text('Manage notification preferences'),
              trailing: const Icon(Icons.chevron_right),
              onTap: () => _showNotificationSettings(
                context,
                settings.notificationSettings,
              ),
            ),
          ],
        ),
      ],
    );
  }

  String _getLanguageName(String code) {
    switch (code) {
      case 'en':
        return 'English';
      case 'es':
        return 'Espa単ol';
      default:
        return 'Unknown';
    }
  }

  Future<void> _showLanguageDialog(BuildContext context, String currentCode) async {
    final languages = {
      'en': 'English',
      'es': 'Espa単ol',
    };

    showDialog(
      context: context,
      builder: (context) => AlertDialog(
        title: const Text('Select Language'),
        content: Column(
          mainAxisSize: MainAxisSize.min,
          children: languages.entries.map((entry) {
            return RadioListTile<String>(
              title: Text(entry.value),
              value: entry.key,
              groupValue: currentCode,
              onChanged: (value) {
                Navigator.pop(context);
                if (value != null) {
                  context.read<SettingsBloc>().add(
                    SettingsEvent.languageChanged(value),
                  );
                }
              },
            );
          }).toList(),
        ),
      ),
    );
  }

  Future<void> _showNotificationSettings(
    BuildContext context,
    NotificationSettings settings,
  ) async {
    showDialog(
      context: context,
      builder: (context) => NotificationSettingsDialog(
        settings: settings,
        onSave: (newSettings) {
          context.read<SettingsBloc>().add(
            SettingsEvent.notificationSettingsChanged(newSettings),
          );
        },
      ),
    );
  }
}
