// lib/features/auth/presentation/widgets/notification_settings_dialog.dart
import 'package:flutter/material.dart';
import '../../domain/entities/notification_settings.dart';

class NotificationSettingsDialog extends StatefulWidget {
  final NotificationSettings settings;
  final ValueChanged<NotificationSettings> onSave;

  const NotificationSettingsDialog({
    super.key,
    required this.settings,
    required this.onSave,
  });

  @override
  State<NotificationSettingsDialog> createState() => _NotificationSettingsDialogState();
}

class _NotificationSettingsDialogState extends State<NotificationSettingsDialog> {
  late bool emailNotifications;
  late bool pushNotifications;
  late bool systemAlerts;
  late bool maintenanceUpdates;

  @override
  void initState() {
    super.initState();
    emailNotifications = widget.settings.emailNotifications;
    pushNotifications = widget.settings.pushNotifications;
    systemAlerts = widget.settings.systemAlerts;
    maintenanceUpdates = widget.settings.maintenanceUpdates;
  }

  @override
  Widget build(BuildContext context) {
    return AlertDialog(
      title: const Text('Notification Settings'),
      content: Column(
        mainAxisSize: MainAxisSize.min,
        children: [
          SwitchListTile(
            title: const Text('Email Notifications'),
            subtitle: const Text('Receive updates via email'),
            value: emailNotifications,
            onChanged: (value) => setState(() => emailNotifications = value),
          ),
          SwitchListTile(
            title: const Text('Push Notifications'),
            subtitle: const Text('Receive push notifications'),
            value: pushNotifications,
            onChanged: (value) => setState(() => pushNotifications = value),
          ),
          SwitchListTile(
            title: const Text('System Alerts'),
            subtitle: const Text('Critical system notifications'),
            value: systemAlerts,
            onChanged: (value) => setState(() => systemAlerts = value),
          ),
          SwitchListTile(
            title: const Text('Maintenance Updates'),
            subtitle: const Text('System maintenance notifications'),
            value: maintenanceUpdates,
            onChanged: (value) => setState(() => maintenanceUpdates = value),
          ),
        ],
      ),
      actions: [
        TextButton(
          onPressed: () => Navigator.pop(context),
          child: const Text('Cancel'),
        ),
        TextButton(
          onPressed: () {
            Navigator.pop(context);
            widget.onSave(
              NotificationSettings(
                emailNotifications: emailNotifications,
                pushNotifications: pushNotifications,
                systemAlerts: systemAlerts,
                maintenanceUpdates: maintenanceUpdates,
              ),
            );
          },
          child: const Text('Save'),
        ),
      ],
    );
  }
}
